<style lang="scss" scoped>
@import '@/assets/scss/config.scss';

.event-card {
  display: flex;
  height: 250px;

  header {
    width: 35%;
    position: relative;

    > div {
      display: flex;
      flex-direction: column;
      height: 100%;
      width: 100%;
      justify-content: space-between;
    }

    svg {
      height: auto;
      width: 100%;
      padding: 0 20px 10px;
    }
  }

  &__time {
    display: flex;
    width: 100%;
    height: 60px;
    justify-content: center;
    align-items: center;
    background-color: rgba($white, .5);
    color: $green;
    font-weight: 800;
    text-transform: uppercase;
    font-size: 14px;

    span {
      padding: 2px 5px;
      background-color: $green;
      color: $white;
      font-size: 24px;
    }

    span:not(:first-child) {
      margin-left: 3px;
    }

    p:not(:first-child) {
      margin-left: 4px;
    }
  }

  &__img {
    height: 100%;
    width: 100%;
    object-fit: cover;
    position: absolute;
    top: 0;
    left: 0;
    z-index: -1;
    border-bottom-left-radius: $border-radius-md;
  }

  &--container {
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    width: 65%;
    background-color: rgba(grey, .4);
    border-top-right-radius: $border-radius-xl;
    border-bottom-right-radius: $border-radius-md;
  }

  &__content {
    height: 100%;
    padding: 0 20px;

    h2 {
      display: flex;
      align-items: center;
      justify-content: center;
      height: 60px;
      width: 100%;
      font-size: 32px;
      border-bottom: 2px solid $white;
    }
  }

  &__details {
    display: flex;
    height: calc(100% - 60px);
    justify-content: space-between;
    align-items: center;
    text-align: center;

    div {
      width: 50%;
    }

    div:first-of-type {
      color: $green;
      font-size: 30px;
      font-weight: 700;
      border-right: 2px solid $white;
      line-height: 1;
    }

    div:last-of-type {
      font-size: 14px;
      padding-left: 10px;

      h3 {
        text-decoration: underline;
      }
    }
  }

  &__social {
    display: flex;
    position: absolute;
    width: auto;
    padding: 12px 4px 8px;
    top: calc(100%);
    left: 0;
    background-color: rgba(grey, .75);
    border-bottom-left-radius: $border-radius-md;
    border-bottom-right-radius: $border-radius-md;
    z-index: 1;

    &-enter-active {
      animation: slide-down .2s forwards;
    }

    &-leave-active {
      animation: slide-down .2s reverse forwards;
    }

    @keyframes slide-down {
      from {
        max-height: 0px;
      }

      to {
        max-height: 48px;
      }
    }
  }

  &__footer {
    display: block;
    position: relative;
    width: 100%;
    padding: 10px 20px;
    background-color: rgba(grey, .6);
    font-size: 12px;
    border-bottom-right-radius: $border-radius-md;

    ul {
      display: flex;
      height: 100%;
    }

    svg {
      height: 30px;
      widows: 30px;
    }
  }
}
</style>
